mapper.select:
SELECT a.*,b.nick_name,b.username
FROM sys_login_record a
left join sys_user b on a.user_id=b.user_id


        AND a.create_time>=#{page.pageData.startDate}


        AND a.create_time<=#{page.pageData.endDate}


        AND b.username like '%${page.pageData.account}%'

SELECT a.*,b.nick_name,b.username
FROM sys_login_record a
left join sys_user b on a.user_id=b.user_id


        AND a.create_time>=#{pageData.startDate}


        AND a.create_time<=#{pageData.endDate}


        AND b.username like '%${pageData.account}%'
